@app.route('/candidate_apply', methods=["POST", "GET"])
@is_logged_in_candidate
def candidate_apply():

    if request.method == 'POST':

        advert_id = request.form["advert_id"]

        candidate = models.Candidates.query.filter_by(email=session["email"]).first()

        job = models.Jobs.query.filter_by(job_id=advert_id).first()

        advert = models.Adverts.query.filter_by(advert_id=advert_id).first()

        employer = models.Employers.query.filter_by(employer_id=advert.employer_id).first()

        if(candidate.resume == None):
            flash("Please upload your resume", "result_feedback")
            return redirect(url_for('search'))
        
        _, f_ext = os.path.splitext(candidate.resume)

        if f_ext == ".pdf":
                
                text_format = ""
                
                path_location = "./static/resumes/" + candidate.resume

                pdf_file = open(path_location, "rb")

                read_object = PyPDF2.PdfFileReader(pdf_file)

                pages = read_object.getNumPages()

                for page_number in range(pages):

                    page = read_object.getPage(page_number)

                    page_content = page.extractText()

                    text_format = text_format + " " + page_content
   
                resume_entities = resume_analyzer(text_format)

        elif f_ext == ".docx":

            path_location = "./static/resumes/" + candidate.resume

            text_format = docx2txt.process(path_location)

            resume_entities = resume_analyzer(text_format)

        entities = " "

        for ent in resume_entities.ents:

             entities = entities + " " + ent.text

        print(entities)

        submission = models.Submissions(entities, candidate.candidate_id, job.job_id, employer.employer_id)

        submission.candidate_id = candidate.candidate_id

        submission.employer_id = employer.employer_id

        submission.job_id = job.job_id

        db.commit()

        get_result(submission)

        flash("You have successfully applied", "result_feedback")
        return redirect(url_for('search'))

    else:
        return redirect(url_for('search'))

def get_result(submission):

    resume_entities = submission.resume_entities

    id = submission.job_id

    print(resume_entities)

    job = models.Jobs.query.filter_by(job_id=id).first()

    advert = models.Adverts.query.filter_by(advert_id=job.job_id).first()

    print(job.job_id)

    advert_entities = job.description_entities

    text = [resume_entities, advert_entities]

    count_vectorizer = CountVectorizer()

    count_matrix = count_vectorizer.fit_transform(text)

    prediction = cosine_similarity(count_matrix)[0][1]

    prediction = prediction * 100

    prediction = round(prediction, 2)

    print(prediction)

    if(prediction >= advert.selection_accuracy):
        print("Candidate Accepted")
    else:
        print("Candidate Rejected")

